import { Component, OnInit } from '@angular/core';
import { SharedService } from 'src/app/shared.service';

interface EmployeeItem {
  EmployeeId: number;
  EmployeeName: string;
  Department: string;
  DateOfJoining: string;
  PhotoFileName: string;
}

@Component({
  selector: 'app-show-emp',
  templateUrl: './show-emp.component.html',
  styleUrls: ['./show-emp.component.css']
})
export class ShowEmpComponent implements OnInit {
  constructor(private service: SharedService) {}
  EmployeeList: EmployeeItem[] = [];

  ModalTitle: string = '';
  ActivateAddEditEmpComp: boolean = false;
  emp: EmployeeItem | null = null;

  ngOnInit(): void {
    this.refreshEmpList();
  }

  addClick() {
    this.emp = {
      EmployeeId: 0,
      EmployeeName: '',
      Department: '',
      DateOfJoining: '',
      PhotoFileName: ''
    };
    this.ModalTitle = 'Add Employee';
    this.ActivateAddEditEmpComp = true;
  }

  deleteClick(item: EmployeeItem) {
    if (confirm('Are you sure?')) {
      this.service.deleteEmployee(item.EmployeeId).subscribe(
        (data) => {
          alert('Employee deleted successfully.');
          this.refreshEmpList();
        },
        (error) => {
          console.error('Error deleting employee:', error);
        }
      );
    }
  }

  closeClick() {
    this.ActivateAddEditEmpComp = false;
    this.refreshEmpList();
  }

  editClick(item: EmployeeItem) {
    console.log(item);
    this.emp = item;
    this.ModalTitle = 'Edit Employee';
    this.ActivateAddEditEmpComp = true;
  }

  refreshEmpList() {
    this.service.getEmpList().subscribe(
      (data: EmployeeItem[]) => {
        this.EmployeeList = data;
      }
    );
  }
}
<!-- Button trigger modal -->
<button type="button" class="btn btn-primary float-right m-2" data-toggle="modal" data-target="#exampleModal" 
(click)="addClick()"
data-backdrop="static" data-keyboard="false"
>
   Add Employee
  </button>

  <!-- Modal -->
  <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div class="modal-dialog modal-dialog-centered modal-xl" role="document">
      <div class="modal-content">
        <div class="modal-header">
          <h5 class="modal-title" id="exampleModalLabel">{{ModalTitle}}</h5>
          <button type="button" class="close" data-dismiss="modal" aria-label="Close"
          (click)="closeClick()"

          >
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
        <div class="modal-body">
        <app-add-edit-emp [emp] ="emp" *ngIf="ActivateAddEditEmpComp">

        </app-add-edit-emp>
        </div>

      </div>
    </div>
  </div>
<table class="table table-striped">

    <thead>
        <tr>
        <th>EmployeeId</th>   
        <th>EmployeeName</th>
        <th>Department</th>
        <th>DateOfJoining</th>
        <th>Options</th>



    </tr>

    </thead>
    <tbody>
        <tr *ngFor="let dataItem of EmployeeList">
            <td>{{dataItem.EmployeeId}}</td>
            <td>{{dataItem.EmployeeName}}</td>
            <td>{{dataItem.Department}}</td>

            <td>{{dataItem.DateOfJoining}}</td>

            <td>
                <button type="button" class="btn btn-light mr-1" data-toggle="modal" data-target="#exampleModal" 
                (click)="editClick(dataItem)"
                data-backdrop="static" data-keyboard="false">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-pencil-fill" viewBox="0 0 16 16">
